import { useState, useReducer } from "react";

const initialState = {
  value: "",
  isTouched: false,
};

const inputStateReducer = (state, action) => {
  return initialState;
};

const useInputForm = (validateValue) => {
  useReducer(inputStateReducer, initialState);


  const validation = validateValue(enteredInput);
  const isInvalid = !validation && touchedInput;

  const onChange = (event) => {
    setEnteredInput(event.target.value);
  };

  const onBlur = () => {
    setTouchedInput(true);
  };

  const reset = () => {
    setEnteredInput("");
    setTouchedInput(false);
  };

  return {
    value: enteredInput,
    valid: validation,
    hasError: isInvalid,
    onChange,
    onBlur,
    reset,
  };
};

export default useInputForm;
