import React, { useEffect, useState } from "react";
import { DUMMY_MEALS } from "../../data/dummy-mels";
import style from "./AvailableMeals.module.css";
import MealItem from "./MealItem/MealItem";
import Card from "../UI/Card/Card";

const AvailableMeals = () => {
  const [allMeals, setAllMeals] = useState([]);

  const fetchMeals = async () => {
    const response = await fetch(
      "https://reactcourse-1e7c6-default-rtdb.firebaseio.com/taks.json",
      {
        method: "GET",
      }
    );

    const responseData = await response.json();

    setAllMeals(responseData.meals)
      .then((response) => response.json())
      .then((data) => setAllMeals(data.meals));
  };

  useEffect(() => {
    fetchMeals();
  }, []);

  console.log(allMeals);
  return (
    <section className={style.meals}>
      <Card>
        <ul>
          {allMeals &&
            allMeals.map((meal) => {
              return <MealItem id={meal.id} key={meal.id} meals={meal} />;
            })}
        </ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
