import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
    totalAmount: 0,
  },
  reducers: {
    addItem(state, payload) {
      state.items.push(payload);
    },
  },
});

export const cartAction = cartSlice.actions;

export default cartSlice.reducer;
