  </Route>
);
const routerWithRoute = createBrowserRouter(routeDefinitions);*/

const router = createBrowserRouter([
  {
    path: "/",
    element: <RootLayout />,
    children: [
      {path: "/", element: <HomePage />,
      },
      { path: "/products", element: <ProductsPage /> },
    ],
  },
]);

function App() {
  return <RouterProvider router={router} />;
}

export default App;
