import React, { useState } from "react";
import style from "./Form.module.css";

const Form = (props) => {
  const [currentSaving, setCurrentSaving] = useState("");
  const [yearlySaving, setYearlySaving] = useState("");
  const [interest, setInterest] = useState("");
  const [duration, setDuration] = useState("");

  const submitForm = (e) => {
    e.preventDefault();

    const formatter = new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    });

    // use like this:
    formatter.format(yourValue);

    const userInputValues = {
      currentSaving: currentSaving,
      yearlySaving: yearlySaving,
      interest: interest,
      duration: duration,
    };

    props.formSubmitted(userInputValues);
  };

  const refreshPage = (e) => {
    e.preventDefault();
    setCurrentSaving("");
    setYearlySaving("");
    setInterest("");
    setDuration("");
  };

  return (
    <form className={style.form}>
      <div className={style.inputGroup}>
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            id="current-savings"
            value={currentSaving}
            onChange={(e) => setCurrentSaving(e.target.value)}
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearly-contribution"
            value={yearlySaving}
            onChange={(e) => setYearlySaving(e.target.value)}
          />
        </p>
      </div>
      <div className={style.inputGroup}>
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            id="expected-return"
            value={interest}
            onChange={(e) => setInterest(e.target.value)}
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            id="duration"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
        </p>
      </div>
      <p className={style.actions}>
        <button type="reset" className={style.buttonAlt} onClick={refreshPage}>
          Reset
        </button>
        <button type="submit" className={style.button} onClick={submitForm}>
          Calculate
        </button>
      </p>
    </form>
  );
};

export default Form;
